/**
 * @param {string} s
 * @param {string} t
 * @return {character}
 */
const findTheDifference = function(s, t) {
  const map = {};
  for (const char of s) {
    map[char] = (map[char] || 0) + 1;
  }

  for (const char of t) {
    if (map[char] !== undefined && map[char] > 0) {
      map[char] -= 1;
    } else {
      return char;
    }
  }
};

console.log(findTheDifference('abcd', 'abcde')); // e
console.log(findTheDifference('ae', 'aea')); // a
